import React from "react";
import './CategoriesChuck.css';
const CategoriesChuck = (props) =>{
    const categoryItemClass = ["category-item"];
    return(
        <div className="categories-wrapper">
            <h1>Categories</h1>
            <div className="category-items-wrapper">
                {props.categories.map((category, index)=>{
                    if(category === 'religion'){
                        categoryItemClass.pop('category-item-left');
                    }
                    else if(category === 'random'){
                        categoryItemClass.pop('category-item-right');
                    }
                    else{
                        categoryItemClass.classList.remove('category-item-left');
                        categoryItemClass.classList.remove('category-item-right');
                    }
                    return(
                        <div className={categoryItemClass} key={index}>
                            {category}
                        </div>
                    )
                })}
            </div>
        </div>
    )
}
export default CategoriesChuck
