import React from "react";
import './CategoriesChuck.css';
const CategoriesChuck = (props) =>{
    let classNameForItem = ["category-item"];
    const generateLink = (category) =>{
        if(category === 'random'){
            props.setActiveJokeLink('https://api.chucknorris.io/jokes/random')
        }
        else{
            props.setActiveJokeLink(`https://api.chucknorris.io/jokes/random${category}`);
        }
    }
    const generateCountOfClick = () =>{
        props.setCounterOfClick(props.counterOfClick+1);
    }
    return(
        <div className="categories-wrapper">
            <h1>Categories</h1>
            <div className="category-items-wrapper">
                {props.categories.map((category, index)=>{
                    if(category === 'religion'){
                        classNameForItem.push("category-item-margin-left");
                    }
                    else if (category === 'random'){
                        classNameForItem.push("category-item-margin-right");
                    }
                    else{
                        classNameForItem = ["category-item"];
                    }
                    return(
                        <div className={classNameForItem.join(' ')}
                             key={index}
                             onClick={()=> {
                                 generateLink(category);
                                 generateCountOfClick();
                             }}
                        >
                            {category}
                        </div>
                    )
                })}
            </div>
        </div>
    )
}
export default CategoriesChuck
